CREATE TABLE IF NOT EXISTS survey_recommendations (
  id SERIAL PRIMARY KEY,
  survey_id INTEGER NOT NULL REFERENCES surveys(id) ON DELETE CASCADE,
  section VARCHAR(50) NOT NULL,
  recommendation TEXT NOT NULL,
  priority VARCHAR(20) DEFAULT 'medium',
  status VARCHAR(20) DEFAULT 'pending',
  modified_by INTEGER REFERENCES users(id),
  modified_at TIMESTAMP,
  created_at TIMESTAMP DEFAULT NOW() NOT NULL
);

CREATE INDEX IF NOT EXISTS survey_recommendations_survey_id_idx ON survey_recommendations(survey_id);
CREATE INDEX IF NOT EXISTS survey_recommendations_status_idx ON survey_recommendations(status);

